let leaf_devices = merge(`analytics:DatasetInfo/Devices`) | where(strContains(_value["hostname"], "-LEAF-"))
let evpnSegStatus = `*:/Sysdb/evpn/status/segmentStatus/*` | where(dictHasKey(leaf_devices, _key))
let remoteTep = `*:/Sysdb/evpn/status/segmentStatus/*/remoteTep` | where(dictHasKey(leaf_devices, _key))
let po_members = `*:/Sysdb/lag/input/interface/lag/intfStatus/*/member` | where(dictHasKey(leaf_devices, _key))
let poIntfStats = `*:/Sysdb/lag/input/interface/lag/intfStatus/*`{"linkStatus"} | where(dictHasKey(leaf_devices, _key))
let allInterfaces = `*:/Sysdb/interface/status/eth/phy/slice/1/intfStatus/*`{"speedEnum"} | where(dictHasKey(leaf_devices, _key))
let rates1M = `analytics:/Devices/*/versioned-data/interfaces/data/*/aggregate/rates/1m`{"inOctets", "outOctets"}

let result = newDict()
let peerCount = newDict()

for key,value in evpnSegStatus{

    for ifkey, ifvalue in value {

        let ivalueDict = merge(ifvalue)
        let esi = str(formatInt(ivalueDict["esid"]["word0"],16)+":") + str(formatInt(ivalueDict["esid"]["word1"],16)+":") + str(formatInt(ivalueDict["esid"]["word2"],16)+":") + str(formatInt(ivalueDict["esid"]["word3"],16)+":") + str(formatInt(ivalueDict["esid"]["word4"],16))

        if !dictHasKey(result, esi) {
            result[esi] = newDict()
        }
        if !dictHasKey(peerCount, esi) {
            peerCount[esi] = 1
        } else {
            peerCount[esi] = peerCount[esi] + 1
        }

        let suffix = leaf_devices[key]["hostname"]
        result[esi][suffix] = newDict()
        result[esi][suffix]

        if dictHasKey(ivalueDict, "intfId") {
            result[esi][suffix]["4_Port-Channel"] = ivalueDict["intfId"]
            if dictHasKey(remoteTep, key) {
                if dictHasKey(remoteTep[key], ivalueDict["intfId"]) {
                    for rTep, rVal in merge(remoteTep[key][ivalueDict["intfId"]]) {
                        result[esi][suffix]["10_Remote-VTEP"] = rTep
                    }
                } else {
                    result[esi][suffix]["10_Remote-VTEP"] = "None"
                }
            }
            if dictHasKey(po_members, key) {
                if dictHasKey(po_members[key], ivalueDict["intfId"]) {
                    let member = str("")
                    for ethKey, ethValue in merge(po_members[key][ivalueDict["intfId"]]) {
                        let member = str(member) + "\n" + str(ethKey)
                        let memberSpeed = merge(allInterfaces[key][ethKey])["speedEnum"]["Name"]
                        let memberInMbps = num(merge(rates1M[key][ethKey])["inOctets"]["avg"]) * 8
                        let memberOutMbps = num(merge(rates1M[key][ethKey])["outOctets"]["avg"]) * 8
                    }
                    result[esi][suffix]["6_Active-Members"] = member
                    result[esi][suffix]["7_Member-Speed"] = strReplace(memberSpeed, "speed", "")
                    result[esi][suffix]["8_Member-In-bps"] = memberInMbps
                    result[esi][suffix]["9_Member-Out-bps"] = memberOutMbps
                } else {
                    result[esi][suffix]["6_Active-Members"] = "None"
                    result[esi][suffix]["7_Member-Speed"] = "Unknown"
                    result[esi][suffix]["8_Member-In-bps"] = "None"
                    result[esi][suffix]["9_Member-Out-bps"] = "None"
                }
            }
            result[esi][suffix]["5_PO_Status"] = merge(poIntfStats[key][ifkey])["linkStatus"]["Name"]
        }
    }

}

let result2 = newDict()
for key,value in result{
    result2[key] = newDict()
    result2[key]["2_Combined_In-bps"] = 0
    result2[key]["3_Combined_Out-bps"] = 0
    for iKey2,iValue2  in value{
        if dictHasKey(result2[key], "1_Name"){
            result2[key]["1_Name"] = str(result2[key]["1_Name"]) + "\n" + strReplace(str(iKey2), "\n", "")
        } else{
            result2[key]["1_Name"] = strReplace(str(iKey2), "\n", "")
        }
        for iKey3,iValue3  in iValue2{
            if iKey3 == "8_Member-In-bps" {
                if str(iValue3) != "None" {
                    result2[key]["2_Combined_In-bps"] = num(result2[key]["2_Combined_In-bps"]) + num(iValue3)
                }
            }
            if iKey3 == "9_Member-Out-bps" {
                if str(iValue3) != "None" {
                    result2[key]["3_Combined_Out-bps"] = num(result2[key]["3_Combined_Out-bps"]) + num(iValue3)
                }
            }
            if dictHasKey(result2[key], iKey3){
                if iKey3 == "8_Member-In-bps" || iKey3 == "9_Member-Out-bps" {
                    if str(iValue3) != "None" {
                        result2[key][iKey3] = str(result2[key][iKey3]) + "\n" + strReplace(str(formatBits(iValue3,0)), "\n", "")
                    }
                } else {
                    result2[key][iKey3] = str(result2[key][iKey3]) + "\n" + strReplace(str(iValue3), "\n", "")
                }

            } else{
                if (iKey3 == "8_Member-In-bps" || iKey3 == "9_Member-Out-bps") {
                     if str(iValue3) != "None" {
                        result2[key][iKey3] = strReplace(str(formatBits(iValue3,0)), "\n", "")
                     }
                } else {
                    result2[key][iKey3] = strReplace(str(iValue3), "\n", "")
                }

            }

        }
    } 
    result2[key]["2_Combined_In-bps"] = formatBits(result2[key]["2_Combined_In-bps"], 0)
    result2[key]["3_Combined_Out-bps"] = formatBits(result2[key]["3_Combined_Out-bps"], 0)
}


